import React, { useState } from "react";
import { data } from "../../../data";
const UseStateArray = () => {
  const [people, setPeople] = useState(data);
  return <React.Fragment>
    <h1>{
      people.map(item => item.name)
    }</h1>
  </React.Fragment>;
};

export default UseStateArray;
